package io.nio.socket;import java.io.IOException;import java.net.InetSocketAddress;import java.nio.ByteBuffer;import java.nio.channels.SocketChannel;/** * @Classname SocketChannel * @Author: hzr * @Description TODO * @Version 1.0.0 * @Date 2023/12/3 15:31 * @Created by 22906 */// Socketchannel对应Socket,tcp/ip的一个链接public class SocketChannelDemo1 {    public static void main(String[] args) throws IOException {        //创建Socketchannel        SocketChannel socketChannel = SocketChannel.open(new InetSocketAddress("www.baidu.com",80));        //设置阻塞和非阻塞        socketChannel.configureBlocking(false);        //读操作        ByteBuffer byteBuffer = ByteBuffer.allocate(16);        socketChannel.read(byteBuffer);        socketChannel.close();        System.out.println("read over");    }}