package oop;/** * @Classname DataStructuresAndAlgorithms.算法.test * @Pakage: oop * @Author: hzr * @Description TODO * @Date 2025/6/27 14:35 * @Created by 22906 * @Version 1.0.0 */public class test {    public static void main(String[] args) {        test();    }    private static void extracted() {        int f[] = new int[10];        f[0] = f[1] = 1;        for (int i = 2; i < 10; i++) {            f[i] = f[i-1] + f[i-2];        }        for (int i = 0; i < 10; i++) {            System.out.print(f[i] + "\t");        }        System.out.println("");    }    public static void test() {        int aMatrix[][] = new int[4][];        int k = 4;        for (int i = 0; i < 4; i++){            aMatrix[i] = new int[k-i];            for (int j = 0; j < aMatrix[i].length; j++){                aMatrix[i][j] = i + 1;                System.out.print(aMatrix[i][j] + "\t");            }            System.out.println();        }    }}