package thread.A_heima.a13waitandnotify;/** * @Classname Cook1 * @Pakage: thread.a13waitandnotify * @Author: hzr * @Description TODO * @Date 2025/6/5 10:21 * @Created by 22906 * @Version 1.0.0 */public class Cook1 extends Thread {    @Override    public void run() {        while (true){            synchronized (Desk.lock) {                if (Desk.count == 0) {                    break;                }else {                    if (Desk.foodFlag == 1){                        try {                            Desk.lock.wait();                            System.out.println("cook1 is waiting");                        } catch (InterruptedException e) {                            throw new RuntimeException(e);                        }                    }else {                        System.out.println("厨师做了一碗面条");                        Desk.foodFlag = 1;                        Desk.lock.notifyAll();                        Desk.count--;                    }                }            }        }    }}