package thread.A_heima.a14waitandnotify;import thread.A_heima.a13waitandnotify.Desk;import java.util.concurrent.ArrayBlockingQueue;/** * @Classname Cook * @Pakage: thread.A_heima.a14waitandnotify * @Author: hzr * @Description TODO * @Date 2025/6/9 11:28 * @Created by 22906 * @Version 1.0.0 */public class Cook extends Thread {    ArrayBlockingQueue<String> queue;    public Cook(ArrayBlockingQueue<String> queue) {        this.queue = queue;    }    @Override    public void run() {        while (true) {            try {                queue.put("面条");                System.out.println("厨师放了一碗面条");            } catch (InterruptedException e) {                e.printStackTrace();            }        }    }}