package thread.A_heima.a14waitandnotify;import java.util.concurrent.ArrayBlockingQueue;/** * @Classname Foodie * @Pakage: thread.A_heima.a14waitandnotify * @Author: hzr * @Description TODO * @Date 2025/6/9 11:29 * @Created by 22906 * @Version 1.0.0 */public class Foodie extends Thread{    ArrayBlockingQueue<String> queue;    public Foodie(ArrayBlockingQueue<String> queue){        this.queue = queue;    }    @Override    public void run() {        while (true){            try {                String food = queue.take();                System.out.println(food);            } catch (InterruptedException e) {                e.printStackTrace();            }        }    }}