package thread.a10threadsafe2;/** * @Classname ThreadDemo * @Pakage: thread.a10threadsafe2 * @Author: hzr * @Description TODO * @Date 2025/5/26 14:49 * @Created by 22906 * @Version 1.0.0 */public class ThreadDemo {    public static void main(String[] args) {        /**         * 需求：         *     某电影院正在上映国产大片，共有100张票，3个窗口卖票，请写一个程序模拟卖票过程。         *     利用同步方法完成         *     技巧：同步代码块         */        MyRunnable myRunnable = new MyRunnable();        Thread t1 = new Thread(myRunnable);        Thread t2 = new Thread(myRunnable);        Thread t3 = new Thread(myRunnable);        t1.setName("窗口1");        t2.setName("窗口2");        t3.setName("窗口3");        t1.start();        t2.start();        t3.start();    }}