package thread.a4threadmethod1;import org.jetbrains.annotations.NotNull;/** * @Classname MyThread * @Pakage: thread.threadmethod4 * @Author: hzr * @Description TODO * @Date 2025/5/26 10:57 * @Created by 22906 * @Version 1.0.0 */public class MyThread extends Thread {    public MyThread() {    }    public MyThread(@NotNull String name) {        super(name);    }    @Override    public void run() {        for (int i = 0; i < 100; i++) {            try {                Thread.sleep(1000);            } catch (InterruptedException e) {                e.printStackTrace();            }            System.out.println(getName() + "@" + i);        }    }}